mapper.select:
select

from tb_playlist playlist


        and playlist.id = #{id}
        and playlist.name = #{name}
        and playlist.create_time = #{createTime}
        and playlist.modified_time = #{modifiedTime}
        and playlist.click_count = #{clickCount}
        and playlist.description = #{description}
        and playlist.cover = #{cover}
        and playlist.user_id = #{userId}

            and playlist.id in (
                select playlist_id from tb_playlist_tag_rt where tag_id = #{tagId}
            )

mapper.update:
update tb_playlist
set click_count = click_count + 1
where id = #{id}

mapper.select:
select

from tb_album album
where id = #{id}

select

from tb_playlist playlist
where playlist.id = #{id}
