mapper.select:
select 
from tb_user user


        and user.id = #{id}
        and user.username = #{username}
        and user.password = #{password}
        and user.email = #{email}
        and user.create_time = #{createTime}
        and user.modified_time = #{modifiedTime}
        and user.avatar = #{avatar}
        and user.role = #{role}

mapper.update:
update tb_user
username = #{username}, password = #{password}, email = #{email}, create_time = #{createTime}, modified_time = #{modifiedTime}, avatar = #{avatar}, role = #{role},
where id = #{id}

mapper.delete:
delete from tb_user


        and id = #{id}
        and username = #{username}
        and password = #{password}
        and email = #{email}
        and create_time = #{createTime}
        and modified_time = #{modifiedTime}
        and avatar = #{avatar}
        and role = #{role}

mapper.select:
select 
from tb_user user


        and user.username = #{username}
        and user.password = #{password}
